mapper.select:
select a.id, a.softwareName,a.APKName,a.supportROM,a.softwareSize,a.devId, (select devName from dev_user where id = a.devId) as devName, a.status, (select valueName from data_dictionary d where a.status=d.valueId AND d.typeCode='APP_STATUS') as statusName, a.flatformId, (select valueName from data_dictionary d where a.flatformId=d.valueId AND d.typeCode='APP_FLATFORM') as flatformName, a.categoryLevel1, (select categoryName from app_category c where c.id=a.categoryLevel1) as categoryLevel1Name, a.categoryLevel2, (select categoryName from app_category c where c.id=a.categoryLevel2) as categoryLevel2Name, a.categoryLevel3, (select categoryName from app_category c where c.id=a.categoryLevel3) as categoryLevel3Name, a.downloads, a.onSaleDate, a.versionId, (select v.versionNo from app_version v where v.id=a.versionId ) as versionNo from app_info a
and a.softwareName like CONCAT ('%',#{softwareName},'%')
and a.status = #{status}
and a.categoryLevel1 = #{categoryLevel1}
and a.categoryLevel2 = #{categoryLevel2}
and a.categoryLevel3 = #{categoryLevel3}
and a.flatformId = #{flatformId}
and a.devId = #{devId}
order by creationDate DESC

select count(*) as count from app_info a


		and a.softwareName like CONCAT ('%',#{softwareName},'%')


		and a.status = #{status}


		and a.categoryLevel1 = #{categoryLevel1}


		and a.categoryLevel2 = #{categoryLevel2}


		and a.categoryLevel3 = #{categoryLevel3}


		and a.flatformId = #{flatformId}


		and a.devId = #{devId}

select * , (select devName from dev_user where id = a.devId) as devName, (select valueName from data_dictionary d where a.status=d.valueId AND d.typeCode='APP_STATUS') as statusName, (select valueName from data_dictionary d where a.status=d.valueId AND d.typeCode='APP_FLATFORM') as flatformName, (select categoryName from app_category c where c.id=a.categoryLevel1) as categoryLevel1Name, (select categoryName from app_category c where c.id=a.categoryLevel2) as categoryLevel2Name, (select categoryName from app_category c where c.id=a.categoryLevel3) as categoryLevel3Name, (select v.versionNo from app_version v where v.id=a.versionId ) as versionNo from app_info a
and a.id = #{id}
and a.APKName = #{APKName}

mapper.update:
update app_info
set versionId=#{versionId}
where id =
#{id}

update app_info
set status=#{status}
where id = #{id}
